    private void btnIngresarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnIngresarMouseClicked
        validarUsuario();
    }//GEN-LAST:event_btnIngresarMouseClicked

    private void txtUsuarioMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_txtUsuarioMouseClicked
        if (txtUsuario.getText().equals("Ingrese su usuario aqui")) {
            txtUsuario.setText("");
            txtUsuario.setForeground(Color.BLACK);
        }

        if (String.valueOf(txtContrasena.getPassword()).isEmpty()) {
            txtContrasena.setText("123456789");
            txtContrasena.setForeground(Color.GRAY);
        }
    }//GEN-LAST:event_txtUsuarioMouseClicked

    private void txtContrasenaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_txtContrasenaMouseClicked
        if (String.valueOf(txtContrasena.getPassword()).equals("123456789")) {
            txtContrasena.setText("");
            txtContrasena.setForeground(Color.BLACK);
        }

        if (txtUsuario.getText().isEmpty()) {
            txtUsuario.setText("Ingrese su usuario aqui");
            txtUsuario.setForeground(Color.GRAY);
        }
    }//GEN-LAST:event_txtContrasenaMouseClicked

