    private int intentosFallidos = 0; // Variable para contar intentos fallidos

    private void validarUsuario() {
        Usuario objUsuario = new Usuario();
        UsuarioDao usuarioDatos = new UsuarioDao();

        objUsuario.setUsuario(txtUsuario.getText());
        objUsuario.setPassword(String.valueOf(txtContrasena.getPassword()));
        objUsuario = usuarioDatos.validarUsuario(objUsuario);

        if (objUsuario != null) {
            JOptionPane.showMessageDialog(this, "BIENVENIDO");
            PrincipalView frmPrincipal = new PrincipalView();
            frmPrincipal.setVisible(true);
            this.dispose();
        } else {
            intentosFallidos++;
            if (intentosFallidos >= 3) {
                JOptionPane.showMessageDialog(this, "Acceso denegado. Se cerrará la aplicación.");
                System.exit(0); // Cierra la aplicación
            } else {
                JOptionPane.showMessageDialog(this, "ACCESO DENEGADO. Intento fallido número " + intentosFallidos);
            }
        }
    }

